element.calculation:
var email = current.email; // Change 'email_field_name' to the actual name of your email field
var emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Regular expression pattern for email validation
if (!emailPattern.test(email)) {
    current.setAbortAction(true); // Prevent saving the record
    gs.addErrorMessage("Invalid email address"); // Add an error message if the email is invalid
}
