sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var userNumber = request.pathParams.number; // Obtém o número do usuário da requisição
	var userTable = 'x_786590_basic_u_0_users';
	var userGR = new GlideRecord(userTable); // Cria um GlideRecord para a tabela de usuários
	userGR.addQuery('number',userNumber)
    // Verifica se o número do usuário foi fornecido
    if (!userNumber) {
        response.setStatus(400); // Define o status da resposta para Bad Request
        return { error: "Número de usuário não fornecido" }; // Retorna uma mensagem de erro
    }

    // Realiza a consulta na tabela de usuários
    if (userGR.get('number', userNumber)) {
        // Se o usuário for encontrado, monta o objeto JSON com as informações do usuário
        var userInfo = {
            sys_id: userGR.getValue('sys_id'),
            name: userGR.getValue('name'),
            email: userGR.getValue('email'),
            // Adicione outras propriedades que deseja retornar aqui
        };

        response.setStatus(200); // Define o status da resposta para OK
        return userInfo; // Retorna as informações do usuário como JSON
    } else {
        // Se o usuário não for encontrado, retorna uma mensagem de erro
        response.setStatus(404); // Define o status da resposta para Not Found
        return { error: "Usuário não encontrado" }; // Retorna uma mensagem de erro
    }

})(request, response);
