sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var userEmail = request.queryParams.email; // Obtém o email do usuário da consulta
    var userTable = 'x_786590_basic_u_0_users';
    var userGR = new GlideRecord(userTable); // Cria um GlideRecord para a tabela de usuários

    // Verifica se o email foi fornecido e adiciona uma condição de consulta se necessário
    if (userEmail) {
        userGR.addQuery('email', userEmail);
    }

    // Realiza a consulta na tabela de usuários
    userGR.query();

    var users = [];
    // Itera sobre os registros encontrados
    while (userGR.next()) {
        // Monta o objeto JSON com as informações do usuário
        var userInfo = {
            sys_id: userGR.getValue('sys_id'),
            name: userGR.getValue('name'),
            email: userGR.getValue('email'),
            // Adicione outras propriedades que deseja retornar aqui
        };
        users.push(userInfo);
    }

    if (users.length > 0) {
        response.setStatus(200); // Define o status da resposta para OK
        return users; // Retorna os usuários como um array JSON
    } else {
        // Se nenhum usuário for encontrado, retorna uma mensagem de erro
        response.setStatus(404); // Define o status da resposta para Not Found
        return { error: "Nenhum usuário encontrado" }; // Retorna uma mensagem de erro
    }

})(request, response);
